import java.io.BufferedReader;import java.io.FileReader;import java.io.IOException;public class Main {public static void main(String[] args) {String inputFile = "Input.txt";try (BufferedReader br = new BufferedReader(new FileReader(inputFile))) {String line;while ((line = br.readLine()) != null) {String[] numbers = line.split(" ");if (numbers.length != 2) {System.out.println("Invalid input format: " + line);continue;}int num1 = Integer.parseInt(numbers[0]);int num2 = Integer.parseInt(numbers[1]);int sum = num1 + num2;System.out.println(sum);}} catch (IOException e) {System.err.println("Error reading from the file: " + e.getMessage());} catch (NumberFormatException e) {System.err.println("Invalid number format in the file: " + e.getMessage());}}}